import { Component } from '@angular/core';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  title = 'frontend';
}
<div class="login-container">
  <h2>Login</h2>
  <form class="login-form" action="your_processing_script.php" method="post">
      <label for="username">Username:</label>
      <input type="text" id="username" name="username" required>

      <label for="password">Password:</label>
      <input type="password" id="password" name="password" required>

      <button type="submit">Login</button>
  </form>
</div>
<script>
  function validateLogin() {
      var usernameInput = document.getElementById('username') ;
      var passwordInput = document.getElementById('password') ;

      var username = usernameInput.value;
      var password = passwordInput.value;

      // Check if the username and password are correct
      if (username === 'admin' && password === 'password') {
          // Redirect to the home page (adjust the URL as needed)
          window.location.href = './homepage/homepage.component.html';
          return false; // Prevent form submission
      } else {
          // Display an error message (you can customize this)
          alert('Invalid username or password');
          return false; // Prevent form submission
      }
  }
</script>
